package com.jeepchief.dh.features.timeline.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.jeepchief.dh.Rimport com.jeepchief.dh.core.network.dto.TimeLineRowsimport com.jeepchief.dh.core.util.Logimport com.jeepchief.dh.core.util.RarityCheckerimport com.jeepchief.dh.databinding.ItemTimeLineBindingclass TimeLineAdapter(private val list: List<TimeLineRows>) : RecyclerView.Adapter<TimeLineAdapter.TimeLineViewHolder>() {    class TimeLineViewHolder(private val binding: ItemTimeLineBinding) : RecyclerView.ViewHolder(binding.root) {        fun bind(timeline: TimeLineRows) {            binding.apply {                Log.e("timeline is >> $timeline")                timeline.run {                    tvDate.text = date.split(" ")[1]                    when(code) {                        101 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_101)                            tvTimeline.text = "아라드에서 모험 시작"                        }                        102 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_102)                        }                        103 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_103)                            tvTimeline.text = String.format(itemView.context.getString(R.string.timeline_job_grown), data.jobGrowName)                        }                        104 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_104)                            tvTimeline.text = itemView.context.getString(R.string.timeline_max_level)                        }                        105 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_105)                        }                        201 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_201)                        }                        202 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_202)                        }                        203 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_203)                        }                        204 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_204)                        }                        205 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_205)                        }                        206 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_206)                        }                        207 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_207)                        }                        208 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_208)                        }                        209 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_209)                        }                        301 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_301)                        }                        401 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_401)                        }                        402 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_402)                        }                        403 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_403)                            tvTimeline.run {                                val result = if(data.result == true) {                                    "성공"                                } else {                                    "실패"                                }                                text = String.format(                                    itemView.context.getString(R.string.timeline_item_refine),                                    data.itemName,                                    data.after,                                    result                                )                                data.itemRarity?.let {                                    setTextColor(RarityChecker.convertColor(it))                                }                            }                        }                        404 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_404)                        }                        405 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_405)                        }                        406 -> {                            getItemMessage(                                this@apply,                                itemView.context.getString(R.string.timeline_code_406),                                String.format(                                    itemView.context.getString(R.string.timeline_item_succession),                                    data.itemName?.plus(" (+${data.reinforce})")                                ),                                data.itemRarity                            )                        }                        501 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_501)                        }                        502 -> {                            getItemMessage(                                this@apply,                                itemView.context.getString(R.string.timeline_code_502),                                String.format(                                    itemView.context.getString(R.string.timeline_get_legendary),                                    data.itemName                                ),                                data.itemRarity                            )                        }                        503 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_503)                        }                        504 -> {                            getItemMessage(                                this@apply,                                itemView.context.getString(R.string.timeline_code_504),                                String.format(                                    itemView.context.getString(R.string.timeline_get_item_pot),                                    data.channelName, data.channelNo, data.itemName                                ),                                data.itemRarity                            )                        }                        505 -> {                            getItemMessage(                                this@apply,                                itemView.context.getString(R.string.timeline_code_505),                                String.format(                                    itemView.context.getString(R.string.timeline_get_item_dungeon),                                    data.channelName, data.channelNo, data.dungeonName, data.itemName                                ),                                data.itemRarity                            )                        }                        506 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_506)                        }                        507 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_507)                        }                        508 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_508)                        }                        509 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_509)                        }                        510 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_510)                        }                        511 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_511)                        }                        512 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_512)                        }                        513 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_513)                        }                        514 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_514)                        }                        515 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_515)                        }                        516 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_516)                        }                        517 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_517)                        }                        518 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_518)                        }                        519 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_519)                        }                        520 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_520)                        }                        601 -> {                            tvTimelineDesc.text = itemView.context.getString(R.string.timeline_code_601)                        }                        602 -> {                            getItemMessage(                                this@apply,                                itemView.context.getString(R.string.timeline_code_602),                                String.format(                                    itemView.context.getString(R.string.timeline_get_talisman),                                    data.itemObtainInfo?.itemName, data.itemObtainInfo?.obtainName, data.itemName                                ),                                data.itemRarity                            )                        }                    }                }            }        }        private fun getItemMessage(binding: ItemTimeLineBinding, desc: String, message: String, itemRarity: String?) {            binding.apply {                tvTimelineDesc.text = desc                tvTimeline.apply {                    text = message                    itemRarity?.let { setTextColor(RarityChecker.convertColor(it)) }                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): TimeLineViewHolder {        val binding = ItemTimeLineBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return TimeLineViewHolder(binding)    }    override fun onBindViewHolder(holder: TimeLineViewHolder, position: Int) {        holder.bind(list[position])    }    override fun getItemCount(): Int = list.size}