package com.jeepchief.dh.view.timeline.adapterimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.jeepchief.dh.Rimport com.jeepchief.dh.model.rest.dto.TimeLineRowsimport com.jeepchief.dh.util.Logimport com.jeepchief.dh.util.RarityCheckerclass TimeLineAdapter(private val list: List<TimeLineRows>) : RecyclerView.Adapter<TimeLineAdapter.TimeLineViewHolder>() {    class TimeLineViewHolder(view: View) : RecyclerView.ViewHolder(view) {        val tvDate: TextView = view.findViewById(R.id.tv_date)        val tvTimeline: TextView = view.findViewById(R.id.tv_timeline)        val tvDesc: TextView = view.findViewById(R.id.tv_timeline_desc)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): TimeLineViewHolder {        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_time_line, parent, false)        return TimeLineViewHolder(view)    }    override fun onBindViewHolder(holder: TimeLineViewHolder, position: Int) {        holder.apply {            list[position].run {                Log.e("timeline is $this")                tvDate.text = date                when(code) {                    101 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_101)                        tvTimeline.text = "아라드에서 모험 시작"                    }                    102 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_102)                    }                    103 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_103)                        tvTimeline.text = String.format(itemView.context.getString(R.string.timeline_job_grown), data.jobGrowName)                    }                    104 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_104)                        tvTimeline.text = itemView.context.getString(R.string.timeline_max_level)                    }                    105 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_105)                    }                    201 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_201)                    }                    202 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_202)                    }                    203 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_203)                    }                    204 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_204)                    }                    205 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_205)                    }                    206 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_206)                    }                    207 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_207)                    }                    208 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_208)                    }                    209 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_209)                    }                    301 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_301)                    }                    401 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_401)                    }                    402 -> {                        tvDesc.text = itemView.context.getString(R.string.timeline_code_402)                    }